    def levelButt(self):
        if self.levelType.currentText() == 'XP':
            n = int(self.levelEnter.text())
            init.xp = init.xp + n
            self.levelSystem()
        if self.levelType.currentText() == 'Milestone':
            n = int(self.levelEnter.text())
            init.lvl = init.lvl + n
            self.levelSystem()

