    def start(self):
        if init.dexMod > -1:
            self.initiativeDisplay.setText(f"+{init.dexMod}")
        self.bgSetup()
    
    def bgSetup(self):
        if self.bgSelect.currentText() == 'Select Background':
            self.bgReset()
        if self.bgSelect.currentText() == 'Acolyte':
            self.bgReset()
            init.insi = True
            init.reli = True
            init.bgLan = 'Pick any 2'
            
    
